import java.util.Scanner;public class JC{	public static void main(String args[])	{        System.out.println(" ");		System.out.println("求一个大于0的整数的阶乘");		System.out.println(" ");		System.out.println("请输入一个大于0的整数");		Scanner sc = new Scanner(System.in);		int a = sc.nextInt();        int result = 1;		if(a>=1&&a<=31)		{			for(int i=1;i<=a;i++)			    result = result*i;			System.out.println("阶乘结果为 "+result);		} 		else    		    System.out.println("错误输入");	}}